<!doctype html>
<html lang="ko">
<head>
    <meta charset="utf-8">
    <style>
        #box {
            position: absolute;
            top: 0;
            left: 0;
            width: 100px;
            height: 100px;
            background-size: contain;
            background-repeat: no-repeat;
            background-image: url("data:image/jpeg;base64,");

        }
    </style>
</head>
<body>
<div id="box"></div>
<script>
    setInterval(function () {
        $('#box').animate({
            left: 100, top: 250
        }, 250, function () {
            $(this).animate({
                left: 200, top: 0
            }, 250, function () {
                $(this).animate({
                    left: 300, top: 100
                }, 250, function () {
                    $(this).animate({
                        left: 200, top: 0
                    }, 250, function () {
                        $(this).animate({
                            left: 100, top: 250
                        }, 250, function () {
                            $(this).animate({
                                left: 0, top: 0
                            }, 250)
                        })
                    })
                })
            })
        })
    }, 1300)
</script>
</body>
</html>